    S_2_low_array = zeros(1, len_X);
    S_2_high_array = zeros(1, len_X);
    for i = 1 : len_X
        _mu_ = mean(X(1:i));
        _S_2_ = var(X(1:i));
        
        mu_array(i) = _mu_;
        S_2_array(i) = _S_2_;
        
        mu_low_array(i) = Find_Mu_Low(i, _mu_, _S_2_, gamma);
        mu_high_array(i) = Find_Mu_High(i, _mu_, _S_2_, gamma);
        
        %R_mu_array(i) = mu_high_array(i) - mu_low_array(i);
        
        S_2_low_array(i) = Find_Sigma_Low(i, _S_2_, gamma);
        S_2_high_array(i) = Find_Sigma_High(i, _S_2_, gamma);
        
        %R_S_2_array(i) = S_2_high_array(i) - S_2_low_array(i);
    end
     
    start_mu = 10;
    figure("numbertitle", "off", ...
           "name", "Графики доверительных интервалов для математического ожидания.", ...
           "position", [10, 100, 1000, 850]);
    plot(start_mu : len_X, [(zeros(1, len_X) + mu)(start_mu : len_X)', mu_array(start_mu : len_X)', mu_low_array(start_mu : len_X)', mu_high_array(start_mu : len_X)']);
    xlabel('n', "fontsize", 15);
    ylabel('y', "fontsize", 15);
    title("Графики доверительных интервалов для математического ожидания.", ...
          "fontsize", 15);
    xlim([start_mu, len_X]);
          
    graph_1 = 'y = hat \mu(vec x_N)';
    graph_2 = 'y = hat \mu(vec x_n)';
    graph_3 = 'y = underline \mu(vec x_n)';
    graph_4 = 'y = overline \mu(vec x_n)';
    legend(graph_1, graph_2, graph_3, graph_4, "fontsize", 15, "location", "southeast");
    grid;
    hold off;
    
    start_S_2 = 15;
    figure("numbertitle", "off", ...
           "name", "Графики доверительных интервалов для дисперсии.", ...
           "position", [10, 100, 1000, 850]);
    plot(start_S_2 : len_X, [(zeros(1, len_X) + S_2)(start_S_2 : len_X)', S_2_array(start_S_2 : len_X)', S_2_low_array(start_S_2 : len_X)', S_2_high_array(start_S_2 : len_X)']);
    xlabel('n', "fontsize", 15);
    ylabel('z', "fontsize", 15);
    title("Графики доверительных интервалов для дисперсии.", ...
          "fontsize", 15);
    xlim([start_S_2, len_X]);
          
    graph_1 = 'y = hat S^2(vec x_N)';
    graph_2 = 'y = hat S^2(vec x_n)';
    graph_3 = 'y = underline \sigma^2(vec x_n)';
    graph_4 = 'y = overline \sigma^2(vec x_n)';
    legend(graph_1, graph_2, graph_3, graph_4, "fontsize", 15);
    grid;
