function mu_low = Find_Mu_Low(n, mu, S_2, gamma)
    mu_low = mu + ((sqrt(S_2) * tinv((1 - gamma) / 2, n - 1)) / sqrt(n));
end

function mu_high = Find_Mu_High(n, mu, S_2, gamma)
    mu_high = mu + ((sqrt(S_2) * tinv((1 + gamma) / 2, n - 1)) / sqrt(n));
end

function sigma_low = Find_Sigma_Low(n, S_2, gamma)
    sigma_low = ((n - 1) * S_2) / chi2inv((1 + gamma) / 2, n - 1);
end

function sigma_high = Find_Sigma_High(n, S_2, gamma)
    sigma_high = ((n - 1) * S_2) / chi2inv((1 - gamma) / 2, n - 1);
end